 // DEPENDENCIES
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { HttpClient } from '@angular/common/http';

// CONFIG
import { environment } from '../../../environments/environment';

// MODEL
import { Utenti } from '../../domain/test_db/utenti';

/**
 * THIS SERVICE MAKE HTTP REQUEST TO SERVER, FOR CUSTOMIZE IT EDIT ../utenti.service.ts
 */

/*
 * SCHEMA DB utenti
 *
	{
		cognome: {
			type: 'String',
			required : true
		},
		nome: {
			type: 'String',
			required : true
		},
		password: {
			type: 'String',
			required : true
		},
		username: {
			type: 'String',
			required : true
		},
		//RELATIONS
		//EXTERNAL RELATIONS
	}
 *
 */
@Injectable()
export class UtentiBaseService {

    contextUrl: string = environment.endpoint + '/utenti';
    constructor(
        protected http: HttpClient
        ) { }

    // CRUD METHODS

    /**
    * utentiService.create
    *   @description CRUD ACTION create
    *
    */
    create(item: Utenti): Observable<Utenti> {
        return this.http
            .post<Utenti>(this.contextUrl, item)
            .pipe(map(data => data));
    }

    /**
    * utentiService.delete
    *   @description CRUD ACTION delete
    *   @param ObjectId id Id
    *
    */
    remove(id: string): Observable<void> {
        return this.http
            .delete<void>(this.contextUrl + '/' + id)
            .pipe(map(data => data));
    }

    /**
    * utentiService.get
    *   @description CRUD ACTION get
    *   @param ObjectId id Id resource
    *
    */
    get(id: string): Observable<Utenti> {
        return this.http
            .get<Utenti>(this.contextUrl + '/' + id)
            .pipe(map(data => data));
    }

    /**
    * utentiService.list
    *   @description CRUD ACTION list
    *
    */
    list(): Observable<Utenti[]> {
        return this.http
            .get<Utenti[]>(this.contextUrl)
            .pipe(map(data => data));
    }

    /**
    * utentiService.update
    *   @description CRUD ACTION update
    *   @param ObjectId id Id
    *
    */
    update(item: Utenti): Observable<Utenti> {
        return this.http
            .post<Utenti>(this.contextUrl + '/' + item._id, item)
            .pipe(map(data => data));
    }


    // Custom APIs

}
